// SPDX-License-Identifier: GPL-3.0
pragma solidity ^0.8.0;
 
contract Ownable {
        address public _owner;
         
        constructor() {
            _owner=msg.sender;
        }
        
        modifier onlyOwner() {
            require(isOwner(), "Ownable: caller is not owner");
            _;
        }
         
         
        function isOwner() public view returns (bool)
        {
            return (msg.sender == _owner);
        }
    }
